def clear_console():
    os.system('cls' if os.name == 'nt' else 'clear')
def redirect_to_main_menu():
    time.sleep(1)
    print("Exiting...")
    time.sleep(1)
    print("Going back to main menu...")
    time.sleep(1)
    clear_console()
    webhookUtils()

